// src/screens/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import debounce from 'lodash.debounce';
import homeStyles from '../styles/home.css';

const Home = () => {
  const [cidadeDigitada, setCidadeDigitada] = useState('');
  const [estadoDigitado, setEstadoDigitado] = useState('');
  const [cidadesEncontradas, setCidadesEncontradas] = useState([]);

  const buscarCidades = async () => {
    try {
      if (cidadeDigitada.trim() !== '' && estadoDigitado.trim() !== '') {
        const response = await axios.get(`https://servicodados.ibge.gov.br/api/v1/localidades/municipios`, {
          params: {
            nome: cidadeDigitada,
            uf: estadoDigitado,
            limit: 10
          }
        });

        console.log(response);

        const cidades = response.data.map((cidade) => ({
          nome: cidade.nome,
          estado: cidade.microrregiao.mesorregiao.UF.sigla
        }));

        setCidadesEncontradas(cidades);
      } else {
        // Limpar a lista quando a entrada está vazia
        setCidadesEncontradas([]);
      }
    } catch (error) {
      console.error('Erro ao buscar cidades:', error);
    }
  };

  const buscarCidadesDebounced = debounce(buscarCidades, 300);

  useEffect(() => {
    buscarCidadesDebounced();
  }, [cidadeDigitada, estadoDigitado]);

  return (
    <div className={homeStyles.body}>
      <div className={homeStyles.searchForm}>
        <input
          type="text"
          className={homeStyles.input}
          placeholder="Nome da Cidade"
          value={cidadeDigitada}
          onChange={(e) => setCidadeDigitada(e.target.value)}
        />
        <input
          type="text"
          className={homeStyles.input}
          placeholder="Estado (UF)"
          value={estadoDigitado}
          onChange={(e) => setEstadoDigitado(e.target.value)}
        />
        <ul>
          {cidadesEncontradas.map((cidade, index) => (
            <li key={index}>{`${cidade.nome}, ${cidade.estado}`}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
