// src/components/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AgendamentoForm from './AgendamentoForm';
import ListaAgendamentos from './ListaAgendamentos';

function App() {
  const [agendamentos, setAgendamentos] = useState([]);

  useEffect(() => {
    // Carregar agendamentos do backend ao montar o componente
    loadAgendamentos();
  }, []);

  const loadAgendamentos = async () => {
    try {
      const response = await axios.get('http://localhost:3001/agendamentos');
      setAgendamentos(response.data);
    } catch (error) {
      console.error('Erro ao carregar agendamentos:', error);
    }
  };

  return (
    <div className="App">
      <h1>Sistema de Agendamento</h1>
      <AgendamentoForm loadAgendamentos={loadAgendamentos} />
      <h2>Agendamentos</h2>
      <ListaAgendamentos agendamentos={agendamentos} />
    </div>
  );
}

export default App;
