import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HorariosPage = ({ cidadeSelecionada, estadoSelecionado }) => {

  const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);

  useEffect(() => {
    const carregarHorariosDisponiveis = async () => {
      try {
        const response = await axios.get('http://localhost:3001/horarios', {
          params: {
            estado: estadoSelecionado,
            cidade: cidadeSelecionada,
          },
        });
        console.log('Horários disponíveis:', response.data);
        setHorariosDisponiveis(response.data);
      } catch (error) {
        console.error('Erro ao carregar horários disponíveis:', error);
      }
    };

    if (cidadeSelecionada && estadoSelecionado) {
      carregarHorariosDisponiveis();
    } else {
      setHorariosDisponiveis([]);
    }
  }, [cidadeSelecionada, estadoSelecionado]);

  // Função para formatar a data no formato dd/mm - hh:mm
  const formatarDataHora = (dataHora) => {
    const data = new Date(dataHora);
    const dia = data.getDate().toString().padStart(2, '0');
    const mes = (data.getMonth() + 1).toString().padStart(2, '0');
    const hora = data.getHours().toString().padStart(2, '0');
    const minutos = data.getMinutes().toString().padStart(2, '0');
    return `${dia}/${mes} - ${hora}:${minutos}`;
  };

  return (
    <div>
      <h2>Horários Disponíveis para Agendamento</h2>
      <div>
        {horariosDisponiveis.map((horario, index) => (
          <button key={index} onClick={() => console.log('Horário selecionado:', horario)}>
            {formatarDataHora(horario.data)} - {horario.hora}
          </button>
        ))}
      </div>
    </div>
  );
};

export default HorariosPage;
