// src/screens/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import debounce from 'lodash.debounce';
import homeStyles from '../styles/home.css';
import Agendamento from './Agendamento';


const Home = () => {
  const [estados, setEstados] = useState([]);
  const [estadoSelecionado, setEstadoSelecionado] = useState('');
  const [cidadeDigitada, setCidadeDigitada] = useState('');
  const [cidadeSelecionada, setCidadeSelecionada] = useState('');
  const [distritosEncontrados, setDistritosEncontrados] = useState([]);
  const [servicoSelecionado, setServicoSelecionado] = useState('');
  const [mostrarAgendamento, setMostrarAgendamento] = useState(false);

  useEffect(() => {
    // Carregar lista de estados ao montar o componente
    const carregarEstados = async () => {
      try {
        const response = await axios.get('https://servicodados.ibge.gov.br/api/v1/localidades/estados');
        const estadosData = response.data.map((estado) => estado.sigla);
        setEstados(estadosData);
      } catch (error) {
        console.error('Erro ao carregar estados:', error);
      }
    };

    carregarEstados();
  }, []);

  const buscarDistritos = async () => {
    try {
      if (estadoSelecionado.trim() !== '' && cidadeDigitada.trim() !== '') {
        const response = await axios.get(`https://servicodados.ibge.gov.br/api/v1/localidades/estados/${estadoSelecionado}/distritos`);

        console.log(response);

        const distritos = response.data
          .filter((distrito) => distrito.nome.toLowerCase().includes(cidadeDigitada.toLowerCase()))
          .map((distrito) => distrito.nome);

        setDistritosEncontrados(distritos);
      } else {
        // Limpar a lista quando a entrada está vazia
        setDistritosEncontrados([]);
      }
    } catch (error) {
      console.error('Erro ao buscar distritos:', error);
    }
  };

  const buscarDistritosDebounced = debounce(buscarDistritos, 300);

  useEffect(() => {
    buscarDistritosDebounced();
  }, [estadoSelecionado, cidadeDigitada]);

  const selecionarCidade = (cidade) => {
    setCidadeDigitada(cidade);
    setCidadeSelecionada(cidade);
    setDistritosEncontrados([]); // Limpar a lista após selecionar uma cidade
  };

  const handlePesquisar = () => {
    // Lógica para ação de pesquisa, você pode implementar de acordo com a necessidade
    console.log('Pesquisar:', estadoSelecionado, cidadeSelecionada);
  };

  const handleServicoSelecionado = (servico) => {
    setServicoSelecionado(servico);
    setMostrarAgendamento(true); // Mostrar a tela de agendamento ao selecionar um serviço
  };


  return (
    <div className={homeStyles.body}>
      {/* ... (restante do código) */}
      <div className={homeStyles.searchForm}>
        {/* Dropdown para selecionar o estado */}
        <select
          className={homeStyles.select}
          value={estadoSelecionado}
          onChange={(e) => setEstadoSelecionado(e.target.value)}
        >
          <option value="">Selecione o Estado</option>
          {estados.map((estado, index) => (
            <option key={index} value={estado}>
              {estado}
            </option>
          ))}
        </select>
        {/* Input para a cidade */}
        <input
          type="text"
          className={homeStyles.input}
          placeholder="Nome da Cidade"
          value={cidadeDigitada}
          onChange={(e) => setCidadeDigitada(e.target.value)}
        />
        {/* Lista de distritos */}
        <ul>
          {distritosEncontrados.map((distrito, index) => (
            <li key={index} onClick={() => selecionarCidade(distrito)}>
              {distrito}
            </li>
          ))}
        </ul>
        {/* Botão de Pesquisar */}
        {cidadeSelecionada && (
          <button className={homeStyles.button} onClick={handlePesquisar}>
            Pesquisar
          </button>
        )}
      </div>

      {mostrarAgendamento && (
        <Agendamento
          cidadeSelecionada={cidadeSelecionada}
          servicoSelecionado={servicoSelecionado}
        />
      )}
    </div>
  );
};

export default Home;
