import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HorariosPage = ({ cidadeSelecionada, estadoSelecionado }) => {

  const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);

  useEffect(() => {
    const carregarHorariosDisponiveis = async () => {
      try {
        const response = await axios.get('http://localhost:3001/horarios', {
          params: {
            estado: estadoSelecionado,
            cidade: cidadeSelecionada,
          },
        });
        console.log('Horários disponíveis:', response.data);
        setHorariosDisponiveis(response.data);
      } catch (error) {
        console.error('Erro ao carregar horários disponíveis:', error);
      }
    };

    if (cidadeSelecionada && estadoSelecionado) {
      carregarHorariosDisponiveis();
    } else {
      setHorariosDisponiveis([]);
    }
  }, [cidadeSelecionada, estadoSelecionado]);

  // Agrupa os horários disponíveis por dia
  const horariosPorDia = {};
  horariosDisponiveis.forEach((horario) => {
    const data = new Date(horario.data).toLocaleDateString('pt-BR');
    if (!horariosPorDia[data]) {
      horariosPorDia[data] = [];
    }
    horariosPorDia[data].push(horario);
  });

// Função para formatar a data no formato dd/mm e o horário no formato hh:mm
const formatarDataHora = (dataHora) => {
  const data = new Date(dataHora);
  const dia = data.getDate().toString().padStart(2, '0');
  const mes = (data.getMonth() + 1).toString().padStart(2, '0');
  const hora = data.getHours().toString().padStart(2, '0');
  const minutos = data.getMinutes().toString().padStart(2, '0');
  return `${dia}/${mes} - ${hora}:${minutos}`;
};





  return (
    <div>
      <h2>Horários Disponíveis para Agendamento</h2>
      <div>
        <table>
          <thead>
            <tr>
              <th>Data</th>
              {Object.keys(horariosPorDia).map((dia, index) => (
                <th key={index}>{dia}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {[...Array(24).keys()].map((hora, index) => (
              <tr key={index}>
                <td>{hora.toString().padStart(2, '0')}:00</td>
                {Object.keys(horariosPorDia).map((dia, index) => {
                  const horariosDoDia = horariosPorDia[dia];
                  const horarioEncontrado = horariosDoDia.find(
                    (horario) => new Date(horario.data).getHours() === hora
                  );
                  return (
                    <td key={index}>
                      {horarioEncontrado ? (
                        <button onClick={() => console.log('Horário selecionado:', horarioEncontrado.hora)}>
                          {formatarDataHora(horarioEncontrado.data)}
                        </button>
                      ) : (
                        <span>-</span>
                      )}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default HorariosPage;
