import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FormularioCadastro from './FormularioCadastro';

const HorariosPage = ({ cidadeSelecionada, estadoSelecionado }) => {
  const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);
  const [horarioSelecionado, setHorarioSelecionado] = useState(null);

  useEffect(() => {
    const carregarHorariosDisponiveis = async () => {
      try {
        const response = await axios.get('http://localhost:3001/horarios', {
          params: {
            estado: estadoSelecionado,
            cidade: cidadeSelecionada,
          },
        });
        setHorariosDisponiveis(response.data);
      } catch (error) {
        console.error('Erro ao carregar horários disponíveis:', error);
      }
    };

    if (cidadeSelecionada && estadoSelecionado) {
      carregarHorariosDisponiveis();
    } else {
      setHorariosDisponiveis([]);
    }
  }, [cidadeSelecionada, estadoSelecionado]);

  const handleHorarioClick = (horarioId) => {
    alert(`Horário ${horarioId} selecionado`);
    setHorarioSelecionado(horarioId);
  };

  return (
    <div>
      {horarioSelecionado ? (
        <FormularioCadastro />
      ) : (
        <div>
          <h2 className="mt-3 mb-4">Horários Disponíveis para Agendamento</h2>
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Data</th>
                  <th>Hora</th>
                </tr>
              </thead>
              <tbody>
                {horariosDisponiveis.map((horario, index) => (
                  <tr key={index}>
                    <td>{horario.data}</td>
                    <td>
                      <button className="btn btn-primary" onClick={() => handleHorarioClick(horario.horario_id)}>
                        {horario.hora}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default HorariosPage;
