// Importe as bibliotecas necessárias e os estilos
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import debounce from 'lodash.debounce';
import 'bootstrap/dist/css/bootstrap.min.css';
import HorariosPage from './HorariosPage';

const Home = () => {
  // Defina os estados necessários
  const [estados, setEstados] = useState([]);
  const [estadoSelecionado, setEstadoSelecionado] = useState('');
  const [cidadeDigitada, setCidadeDigitada] = useState('');
  const [cidadeSelecionada, setCidadeSelecionada] = useState('');
  const [distritosEncontrados, setDistritosEncontrados] = useState([]);
  const [mostrarHorario, setMostrarHorario] = useState(false);

  // Função para carregar os estados
  useEffect(() => {
    const carregarEstados = async () => {
      try {
        const response = await axios.get('https://servicodados.ibge.gov.br/api/v1/localidades/estados');
        setEstados(response.data);
      } catch (error) {
        console.error('Erro ao carregar estados:', error);
      }
    };

    carregarEstados();
  }, []);

  // Função para buscar os distritos
  const buscarDistritos = async () => {
    try {
      if (estadoSelecionado.trim() !== '' && cidadeDigitada.trim() !== '') {
        const response = await axios.get(`https://servicodados.ibge.gov.br/api/v1/localidades/estados/${estadoSelecionado}/distritos`);

        const distritos = response.data
          .filter((distrito) => distrito.nome.toLowerCase().includes(cidadeDigitada.toLowerCase()))
          .map((distrito) => ({
            id: distrito.id,
            nome: distrito.nome,
          }));

        setDistritosEncontrados(distritos);
      } else {
        setDistritosEncontrados([]);
      }
    } catch (error) {
      console.error('Erro ao buscar distritos:', error);
    }
  };

  // Função de debounce para buscar os distritos
  const buscarDistritosDebounced = debounce(buscarDistritos, 300);

  // Efeito para executar a busca dos distritos
  useEffect(() => {
    buscarDistritosDebounced();
  }, [estadoSelecionado, cidadeDigitada]);

  // Função para selecionar a cidade
  const selecionarCidade = (cidade) => {
    setCidadeDigitada(cidade.nome);
    setCidadeSelecionada(cidade.id);
    setDistritosEncontrados([]);
  };

  // Função para lidar com o clique em Pesquisar
  const handlePesquisarClick = () => {
    setMostrarHorario(true); // Mostra os horários ao clicar em Pesquisar
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6 offset-md-3">
          {/* Renderiza o formulário de pesquisa somente se mostrarHorario for falso */}
          {!mostrarHorario && (
            <div className="card">
              <div className="card-body">
                <h2 className="card-title text-center mb-4">Pesquisar Horários</h2>
                {/* Formulário de pesquisa */}
                {/* Coloque aqui os elementos do formulário de pesquisa */}
                {/* Aqui ficará o código do seu formulário de pesquisa */}
                <button
                  className="btn btn-primary btn-block"
                  onClick={handlePesquisarClick}
                  disabled={!cidadeSelecionada}
                >
                  Pesquisar
                </button>
              </div>
            </div>
          )}
          {/* Renderiza a página de horários se mostrarHorario for verdadeiro */}
          {mostrarHorario && (
            <HorariosPage
              cidadeSelecionada={cidadeSelecionada}
              estadoSelecionado={estadoSelecionado}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
