import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FormularioCadastro from './FormularioCadastro';

const HorariosPage = ({ cidadeSelecionada, estadoSelecionado }) => {
  const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);
  const [horarioSelecionado, setHorarioSelecionado] = useState(null);

  useEffect(() => {
    const carregarHorariosDisponiveis = async () => {
      try {
        const response = await axios.get('http://localhost:3001/horarios', {
          params: {
            estado: estadoSelecionado,
            cidade: cidadeSelecionada,
          },
        });
        setHorariosDisponiveis(response.data);
      } catch (error) {
        console.error('Erro ao carregar horários disponíveis:', error);
      }
    };

    if (cidadeSelecionada && estadoSelecionado) {
      carregarHorariosDisponiveis();
    } else {
      setHorariosDisponiveis([]);
    }
  }, [cidadeSelecionada, estadoSelecionado]);

  const handleHorarioClick = (horarioId) => {
    alert(`Horário ${horarioId} selecionado`);
    setHorarioSelecionado(horarioId);
  };

  const agruparHorariosPorUsuarioEData = () => {
    const horariosAgrupados = {};
    horariosDisponiveis.forEach((horario) => {
      const chaveUsuario = horario.nome_usuario;
      const chaveData = horario.data;
      if (!horariosAgrupados[chaveUsuario]) {
        horariosAgrupados[chaveUsuario] = {};
      }
      if (!horariosAgrupados[chaveUsuario][chaveData]) {
        horariosAgrupados[chaveUsuario][chaveData] = [];
      }
      horariosAgrupados[chaveUsuario][chaveData].push(horario);
    });
    return horariosAgrupados;
  };

  return (
    <div>
      {horarioSelecionado ? (
        <FormularioCadastro />
      ) : (
        <div>
          <h2 className="mt-3 mb-4">Horários Disponíveis para Agendamento</h2>
          <div className="card">
            <div className="card-body">
              {Object.keys(agruparHorariosPorUsuarioEData()).map((chaveUsuario, index) => (
                <div className="card mb-3" key={index}>
                  <div className="card-body">
                    <h5 className="card-title">{chaveUsuario}</h5>
                    <div className="row">
                      {Object.keys(agruparHorariosPorUsuarioEData()[chaveUsuario]).map((chaveData, index) => (
                        <div className="col" key={index}>
                          <h6 className="card-subtitle mb-2 text-muted">{horario.data_reduzida}</h6>
                          {agruparHorariosPorUsuarioEData()[chaveUsuario][chaveData].map((horario, index) => (
                            <div key={index}>
                              <button className="btn btn-primary mb-2" onClick={() => handleHorarioClick(horario.horario_id)}>
                                {horario.hora}
                              </button>
                            </div>
                          ))}
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HorariosPage;
