// src/screens/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import homeStyles from '../styles/home.css';  // Importando os estilos

const Home = () => {
  const [cidadeDigitada, setCidadeDigitada] = useState('');
  const [cidadesEncontradas, setCidadesEncontradas] = useState([]);

  useEffect(() => {
    // Consultar a API ao digitar o nome da cidade
    if (cidadeDigitada.trim() !== '') {
      buscarCidades();
    } else {
      // Limpar a lista quando a entrada está vazia
      setCidadesEncontradas([]);
    }
  }, [cidadeDigitada]);

  const buscarCidades = async () => {
    try {
      const response = await axios.get(`https://servicodados.ibge.gov.br/api/v1/localidades/municipios?nome=${cidadeDigitada}&orderBy=nome`);
      const cidades = response.data.map((cidade) => cidade.nome);
      setCidadesEncontradas(cidades);
    } catch (error) {
      console.error('Erro ao buscar cidades:', error);
    }
  };

  return (
    <div className={homeStyles.body}>
      {/* ... (restante do código) */}
      <div className={homeStyles.searchForm}>
        <h2>Consultar Cidades</h2>
        <input
          type="text"
          className={homeStyles.input}
          placeholder="Nome da Cidade"
          value={cidadeDigitada}
          onChange={(e) => setCidadeDigitada(e.target.value)}
        />
        <button
          className={homeStyles.button}
        >
          Pesquisar
        </button>
        {/* Exibir a lista de cidades encontradas */}
        <ul>
          {cidadesEncontradas.map((cidade, index) => (
            <li key={index}>{cidade}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
