// Importe as bibliotecas necessárias e os componentes
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FormularioCadastro from './FormularioCadastro';

const HorariosPage = ({ cidadeSelecionada, estadoSelecionado }) => {
  // Defina os estados necessários
  const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);
  const [horarioSelecionado, setHorarioSelecionado] = useState(null);

  // UseEffect para carregar os horários disponíveis
  useEffect(() => {
    const carregarHorariosDisponiveis = async () => {
      try {
        const response = await axios.get('http://localhost:3001/horarios', {
          params: {
            estado: estadoSelecionado,
            cidade: cidadeSelecionada,
          },
        });
        setHorariosDisponiveis(response.data);
      } catch (error) {
        console.error('Erro ao carregar horários disponíveis:', error);
      }
    };

    if (cidadeSelecionada && estadoSelecionado) {
      carregarHorariosDisponiveis();
    } else {
      setHorariosDisponiveis([]);
    }
  }, [cidadeSelecionada, estadoSelecionado]);

  // Função para lidar com o clique em um horário
  const handleHorarioClick = (horarioId) => {
    alert(`Horário ${horarioId} selecionado`);
    setHorarioSelecionado(horarioId);
  };

  // Função para agrupar os horários por data
  const agruparHorariosPorData = () => {
    const horariosAgrupados = {};
    horariosDisponiveis.forEach((horario) => {
      const data = horario.data; // Supondo que 'data' seja a propriedade correta do objeto
      if (!horariosAgrupados[data]) {
        horariosAgrupados[data] = [];
      }
      horariosAgrupados[data].push(horario);
    });
    return horariosAgrupados;
  };

  // Renderização do componente
  return (
    <div>
      {/* Verifica se algum horário foi selecionado */}
      {horarioSelecionado ? (
        <FormularioCadastro />
      ) : (
        <div>
          <h2 className="mt-3 mb-4">Horários Disponíveis para Agendamento</h2>
          <div className="card">
            <div className="card-body">
              {/* Mapeia os horários agrupados por data */}
              {Object.keys(agruparHorariosPorData()).map((data, index) => (
                <div className="card mb-3" key={index}>
                  <div className="card-body">
                    <h5 className="card-title">Data: {data}</h5>
                    {/* Mapeia os horários de cada data */}
                    {agruparHorariosPorData()[data].map((horario, index) => (
                      <div key={index}>
                        <p className="card-text">Hora: {horario.hora}</p>
                        {/* O botão é o próprio horário */}
                        <button className="btn btn-primary" onClick={() => handleHorarioClick(horario.horario_id)}>
                          Selecionar
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HorariosPage;
