import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [agendamentos, setAgendamentos] = useState([]);
  const [nomeCliente, setNomeCliente] = useState('');
  const [descricaoServico, setDescricaoServico] = useState('');

  useEffect(() => {
    // Carregar agendamentos do backend ao montar o componente
    loadAgendamentos();
  }, []);

  const loadAgendamentos = async () => {
    try {
      const response = await axios.get('http://localhost:3001/agendamentos');
      setAgendamentos(response.data);
    } catch (error) {
      console.error('Erro ao carregar agendamentos:', error);
    }
  };

  const handleAgendamentoSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/agendamentos', {
        nome_cliente: nomeCliente,
        descricao_servico: descricaoServico
      });
      // Atualizar a lista de agendamentos após adicionar um novo
      loadAgendamentos();
      // Limpar campos do formulário
      setNomeCliente('');
      setDescricaoServico('');
    } catch (error) {
      console.error('Erro ao adicionar agendamento:', error);
    }
  };

  return (
    <div className="App">
      <h1>Sistema de Agendamento</h1>
      <form onSubmit={handleAgendamentoSubmit}>
        <label>Nome do Cliente:</label>
        <input
          type="text"
          value={nomeCliente}
          onChange={(e) => setNomeCliente(e.target.value)}
        />
        <br />
        <label>Descrição do Serviço:</label>
        <textarea
          value={descricaoServico}
          onChange={(e) => setDescricaoServico(e.target.value)}
        ></textarea>
        <br />
        <button type="submit">Agendar</button>
      </form>
      <h2>Agendamentos</h2>
      <ul>
        {agendamentos.map((agendamento) => (
          <li key={agendamento.id}>
            <strong>{agendamento.nome_cliente}</strong> - {agendamento.descricao_servico}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
